const Root = styled('svg')``;

type Props = IconProps & {
  type: ExpertiseIconType;
};

const ExpertiseIcon = ({ type, ...props }: Props) => {
  let icon: ComponentType<IconProps>;

  switch (type) {
    case ExpertiseIconType.HTML:
      icon = Html;
      break;
    case ExpertiseIconType.CSS:
      icon = Css;
      break;
    case ExpertiseIconType.JAVASCRIPT:
      icon = JavaScript;
      break;
    case ExpertiseIconType.TYPESCRIPT:
      icon = TypeScript;
      break;
    case ExpertiseIconType.DART:
      icon = Dart;
      break;
    case ExpertiseIconType.NODEJS:
      icon = Nodejs;
      break;
    case ExpertiseIconType.REACT:
      icon = React;
      break;
    case ExpertiseIconType.GRAPHQL:
      icon = GraphQL;
      break;
    case ExpertiseIconType.FLUTTER:
      icon = Flutter;
      break;
    default:
      throw new Error();
  }

  return createElement(icon, props);
};

export default ExpertiseIcon;
